import { useEffect, useState } from 'react'
import Nav from './Nav.jsx';
import './App.css';

function App() {
  const [characters, setCharacters] = useState([])
  const [character, setCharacter] = useState({})
  let [description, setDescription] = useState(false)

  useEffect(() => {
    apiCall()
  }, [])

  function apiCall() {
    fetch(" https://rickandmortyapi.com/api/character")
      .then((res) => res.json())
      .then((data) => setCharacters(data.results))
  }

  function display() {
     setDescription(prev => !prev)
  } 

  function handleClick(charInfo) {
    setCharacter(charInfo)
  }

  return (
    <div className="App">
      <Nav />
      <button className='button' onClick={display}>Character Information</button>
      {characters.map((character, index) => (
        <div onClick={() => handleClick(character)} key={index}>
          <img src={character.image} />
          <h3>{character.name}</h3>
        </div>
      ))}
      {description ?
        <>
          <p>Status - {character.status}</p>
          <p>Species - {character.species}</p>
          <p>Gender - {character.gender}</p>
          <p>Origin - {character.origin.name}</p>
          <p>Location - {character.location.name}</p>
        </>
        :
        null
      }
    </div>
  );
}

export default App;
